def get_total_profit(days):
    total_profit = 0
    for item in all_stock_tips:
        for company in item:
            for tip in company:
                stock = tip[0]
                mydate = tip[2]

                #sell after one day
                later_date = str(get_later_date(mydate, days)) #one day later etc.
                open1, close1, high, low = get_stock_on_date(stock, mydate)
                print(stock, later_date)
                open2, close2, high, low = get_stock_on_date(stock, later_date)
                profit = num(open2) - num(open1)
                total_profit += profit
    return total_profit

